targets.expr:
(onprem_host_memory_usage_used{account_id=~"$account_id", ophid=~"$ophid"}
  * on(ophid) group_left(service)
  onprem_host_service_status{service=~"cdc.*"}) /
(onprem_host_memory_usage_total{account_id=~"$account_id", ophid=~"$ophid"}
  * on(ophid) group_left(service)
  onprem_host_service_status{service=~"cdc.*"}) *100 > $memory_threshold
